INSERT INTO `wp_posts` VALUES("1568", "1", "2016-08-13 17:36:58", "2016-08-13 17:36:58", "<p style=\"text-align: justify;\">At Underdogz Gym we have had great success in transforming peoples figures and improving their health as well as their fitness, with our training and nutrition plans we make sure that you only lose body fat and not muscle, this is what give you the toned defined look. We have had too many success stories to mention but please check out before and afters for just a couple of the great examples of this.</p>", "Fit Box Classes", "", "inherit", "closed", "closed", "", "1049-autosave-v1", "", "", "2016-08-13 17:36:58", "2016-08-13 17:36:58", "", "1049", "http://live.underdogz.co.uk/1049-autosave-v1/", "0", "revision", "", "0");
